import React from 'react';
import './DigitalMenu.css';

const DigitalMenu: React.FC = () => {
  // Scroll function for gallery arrows
  const scrollGallery = (direction: 'left' | 'right') => {
    const gallery = document.getElementById('gallery');
    if (gallery) {
      const scrollAmount = gallery.clientWidth; // Scroll by the width of the visible area (3 images)
      gallery.scrollBy({
        left: direction === 'left' ? -scrollAmount : scrollAmount,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="digital-menu">
      {/* Header */}
      <header className="menu-header">
        <img src="/logo.png" alt="Restaurant Logo" className="menu-logo" />
        <h1 className="menu-title">DishUp</h1>
      </header>

      {/* Menu */}
      <main className="menu-section">
        <h2>Our Menu</h2>
        <ul className="menu-list">
          <li>üçî Signature Burger - $10</li>
          <li>üçù Creamy Pasta - $15</li>
          <li>ü•ó Fresh Salad - $12</li>
        </ul>
      </main>

      {/* Gallery with arrows */}
      <footer className="menu-footer">
        <h3>Gallery</h3>
        <div className="gallery-wrapper">
          <button className="arrow left" onClick={() => scrollGallery('left')}>&#10094;</button>

          <div className="menu-gallery" id="gallery">
            <img src="/pic1.jpg" alt="Dish 1" className="gallery-image" />
            <img src="/pic2.jpg" alt="Dish 2" className="gallery-image" />
            <img src="/pic3.jpg" alt="Dish 3" className="gallery-image" />
            <img src="/pic4.jpg" alt="Dish 4" className="gallery-image" />
            <img src="/pic5.jpg" alt="Dish 5" className="gallery-image" />
            <img src="/pic6.jpg" alt="Dish 6" className="gallery-image" />
            <img src="/pic7.jpg" alt="Dish 7" className="gallery-image" />
            <img src="/pic8.jpg" alt="Dish 8" className="gallery-image" />
          </div>

          <button className="arrow right" onClick={() => scrollGallery('right')}>&#10095;</button>
        </div>
      </footer>
    </div>
  );
};

export default DigitalMenu;
