import React, { useState } from 'react';
import './DigitalMenu.css';

const images = [
  { src: "/dish1.jpg", alt: "Dish 1" },
  { src: "/dish2.jpg", alt: "Dish 2" },
  { src: "/dish3.jpg", alt: "Dish 3" },
  { src: "/dish4.jpg", alt: "Dish 4" },
  { src: "/dish5.jpg", alt: "Dish 5" },
  { src: "/dish6.jpg", alt: "Dish 6" },
  { src: "/dish7.jpg", alt: "Dish 7" },
  { src: "/dish8.jpg", alt: "Dish 8" },
];

const DigitalMenu: React.FC = () => {
  const [startIndex, setStartIndex] = useState(0);
  const visibleImages = images.slice(startIndex, startIndex + 3);

  const handleScroll = (direction: 'left' | 'right') => {
    if (direction === 'left' && startIndex > 0) {
      setStartIndex(startIndex - 3);
    } else if (direction === 'right' && startIndex + 3 < images.length) {
      setStartIndex(startIndex + 3);
    }
  };

  return (
    <div className="digital-menu">
      {/* HEADER + MENU - keep as-is */}
      <header className="menu-header">
        <img src="logo.png" alt="Restaurant Logo" className="menu-logo" />
        <h1 className="menu-title">DishUp</h1>
      </header>

      <main className="menu-section">
        <h2>Our Menu</h2>
        <ul className="menu-list">
          <li>üçî Signature Burger - $10</li>
          <li>üçù Creamy Pasta - $15</li>
          <li>ü•ó Fresh Salad - $12</li>
        </ul>
      </main>

      {/* GALLERY with 3-at-a-time carousel */}
      <footer className="menu-footer">
        <h3>Gallery</h3>
        <div className="carousel-wrapper">
          <button className="arrow left" onClick={() => handleScroll('left')} disabled={startIndex === 0}>
            &#10094;
          </button>

          <div className="carousel-gallery">
            {visibleImages.map((img, index) => (
              <img key={index} src={img.src} alt={img.alt} className="gallery-image" />
            ))}
          </div>

          <button className="arrow right" onClick={() => handleScroll('right')} disabled={startIndex + 3 >= images.length}>
            &#10095;
          </button>
        </div>
      </footer>
    </div>
  );
};

export default DigitalMenu;
